
public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		Variables
		int a=10;
		int b=20;
		
//		Arithmetic Operators 
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
//		Relational Operators
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a>=b);
		System.out.println(a<=b);
		System.out.println(a!=b);
		System.out.println(a==b);
		
//		Logical Operators
		System.out.println(a&b);
		
	}

}
